import{_ as e,c as t}from"./app.a74ffd8d.js";const o={},i=t('<h1 id="mobilenets-ssd" tabindex="-1"><a class="header-anchor" href="#mobilenets-ssd" aria-hidden="true">#</a> MobileNets-SSD</h1><h2 id="\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#\u533A\u522B" aria-hidden="true">#</a> \u533A\u522B</h2><p>MobileNets\u76F8\u8F83\u4E8E\u6807\u51C6\u5377\u79EF\u64CD\u4F5C\u6709\u4EE5\u4E0B\u4F18\u5316</p><h3 id="\u6DF1\u5EA6\u53EF\u5206\u79BB\u5377\u79EF-depthwise-separable-convolution" tabindex="-1"><a class="header-anchor" href="#\u6DF1\u5EA6\u53EF\u5206\u79BB\u5377\u79EF-depthwise-separable-convolution" aria-hidden="true">#</a> \u6DF1\u5EA6\u53EF\u5206\u79BB\u5377\u79EF (Depthwise Separable Convolution)</h3><p>depthwise separable convolutions\u5C06\u5377\u79EF\u62C6\u5206\u6210\u6EE4\u6CE2\u4E0Ecombine\u4E24\u5C42\u6765\u5B8C\u6210\uFF0Cfactorization\u6781\u5927\u7684\u4F18\u5316\u4E86\u8BBE\u5907\u8BA1\u7B97\u91CF\u548C\u6A21\u578B\u5927\u5C0F\u3002\u800C\u6807\u51C6\u7684\u5377\u79EF\u662F\u5C06kernel\u548Ccombine\u540C\u65F6\u5728output channels\u8FD9\u4E00\u6B65\u9AA4\u4E2D\u5B8C\u6210\u3002</p><h3 id="\u9010\u70B9\u5377\u79EF-pointwise-convolution" tabindex="-1"><a class="header-anchor" href="#\u9010\u70B9\u5377\u79EF-pointwise-convolution" aria-hidden="true">#</a> \u9010\u70B9\u5377\u79EF (Pointwise convolution)</h3><p>pointwise convolution\u5373\u6807\u51C6\u5377\u79EF\u4F46\u4F7F\u7528\u7684\u662F1x1\u7684\u5377\u79EF\u6838\uFF0C\u8FD9\u5927\u5927\u964D\u4F4E\u4E86\u8BA1\u7B97\u91CF\u3002</p><h1 id="depthwise-separable-convolution" tabindex="-1"><a class="header-anchor" href="#depthwise-separable-convolution" aria-hidden="true">#</a> Depthwise Separable Convolution</h1><p>\u4E00\u4E2A\u6807\u51C6\u7684\u5377\u79EF\u5C42\u8F93\u5165\u7EF4\u5EA6\u4E3A $D_{F} \\times D_{F} \\times M$ \u7684\u7279\u5F81\u56FE\u8C31 $F$\uFF0C\u4ECE\u800C\u4EA7\u751F $D_{G} \\times D_{G} \\times N$ \u7684\u7279\u5F81\u56FE\u8C31 $G$\uFF0C\u5176\u4E2D $D_{F}$ \u662F\u6B63\u65B9\u5F62\u8F93\u5165\u7279\u5F81\u56FE\u8C31\u7684\u7A7A\u95F4\u5BBD\u548C\u9AD8\uFF0CM\u662F\u8F93\u5165\u901A\u9053\u7684\u6570\u91CF(input depth)\uFF0C$D_{G}$\u662F\u6B63\u65B9\u5F62\u8F93\u51FA\u7279\u5F81\u56FE\u8C31\u7684\u7A7A\u95F4\u5BBD\u548C\u9AD8\uFF0CN\u662F\u8F93\u51FA\u901A\u9053\u7684\u6570\u91CF(output depth)\u3002\u6807\u51C6\u5377\u79EF\u5C42\u7531\u5927\u5C0F\u4E3A $D_{K} \\times D_{k} \\times M \\times N$ \u7684\u5377\u79EF\u6838 K \u53C2\u6570\u5316\uFF0C\u5176\u4E2D $D_{K}$ \u5047\u5B9A\u4E3A\u6B63\u65B9\u5F62\u5185\u6838\u7684\u7A7A\u95F4\u7EF4\u5EA6\uFF0C$M$ \u662F\u8F93\u5165\u901A\u9053\u6570(input depth)\uFF0C$N$ \u662F\u524D\u9762\u5B9A\u4E49\u7684\u8F93\u51FA\u901A\u9053\u6570(output depth)\u3002</p><p>\u5982\u679C\u5047\u8BBE\u5377\u79EFstride\u662F1\u5E76\u8003\u8651padding\uFF0C\u90A3\u4E48\u6807\u51C6\u5316\u5377\u79EF\u64CD\u4F5C\u7684\u8F93\u51FA\u7279\u5F81\u56FE\u8C31\u4E3A:</p><p>$$G_{k,l,n} = \\sum_{i,j,m} K_{i,j,m,n} \\cdot F_{k+i-1,l+j-1,m}$$</p><p>\u6807\u51C6\u5377\u79EF\u7684\u8BA1\u7B97\u590D\u6742\u5EA6\u4E3A:</p><p>$$D_{K} \\cdot D_{K} \\cdot M \\cdot N \\cdot D_{F} \\cdot D_{F}$$</p><p>\u5176\u4E2D\u8BA1\u7B97\u6210\u672C\u6210\u500D\u7684\u53D6\u51B3\u4E8E\u8F93\u5165\u901A\u9053\u7684\u6570\u91CF $M$ \u3001\u8F93\u51FA\u901A\u9053\u7684\u6570\u91CF $N$ \u4EE5\u53CA\u5377\u79EF\u6838 $D_{K} \\times D_{K}$ \u548C\u7279\u5F81\u56FE\u5927\u5C0F $D_{K} \\times D_{K}$ \u3002</p><p>\u6807\u51C6\u7684\u5377\u79EF\u64CD\u4F5C\u4E3A\u4E86\u751F\u6210\u65B0\u7684\u8868\u793A\u9700\u8981\u57FA\u4E8E\u5377\u79EF\u6838\u548Ccombine\u7279\u5F81\u6765\u8FC7\u6EE4\u7279\u5F81\u3002\u8FD9\u79CD\u6EE4\u6CE2\u548Ccombine\u64CD\u4F5C\u53EF\u4EE5\u901A\u8FC7\u4F7F\u7528\u5206\u89E3\u5377\u79EF(factorized convolutions)\u6765\u5927\u5E45\u964D\u4F4E\u8BA1\u7B97\u590D\u6742\u5EA6\u3002</p><p>\u6DF1\u5EA6\u53EF\u5206\u79BB\u5377\u79EF\u7531\u4E24\u90E8\u5206\u7EC4\u6210: depthwise convolutions\u548Cpointwise convolutions\u3002depthwise convolutions \u5BF9\u6BCF\u4E2A\u8F93\u5165\u901A\u9053(input depth)\u5E94\u7528\u5355\u4E00\u6EE4\u6CE2\u5668\uFF0Cointwise convolutions\u7528\u4E8E\u521B\u5EFA\u6DF1\u5EA6\u5C42\u8F93\u51FA\u7684\u7EBF\u6027\u53E0\u52A0\u3002MobileNets \u5BF9\u4E24\u4E2A\u5C42\u90FD\u4F7F\u7528\u4E86 batchnorm \u548C ReLU \u975E\u7EBF\u6027\u6FC0\u6D3B\u5355\u5143\u3002</p><p>\u6BCF\u4E2A\u8F93\u5165\u901A\u9053\u4E00\u4E2A\u6EE4\u6CE2\u5668\u7684depthwise convolution\u53EF\u4EE5\u5199\u4E3A:</p><p>$$\\widehat{G}<em>{k,l,n} = \\sum</em>{i,j} \\widehat{K}<em>{i,j,m} \\cdot F</em>{k+i-1,l+j-1,m}$$</p><p>\u5176\u4E2D $\\widehat{K}$ \u662F\u5927\u5C0F\u4E3A $D_{K} \\times D_{k} \\times M$ \u7684depthwise convolution\u7684\u5377\u79EF\u6838\uFF0C\u5176\u4E2D $\\widehat{K}$ \u7684\u7B2Cm\u4E2A\u6EE4\u6CE2\u5668\u88AB\u5E94\u7528\u4E8E F \u4E2D\u7684\u7B2C m \u4E2A\u901A\u9053\uFF0C\u7528\u6765\u751F\u6210\u8FC7\u6EE4\u540E\u7684\u8F93\u51FA\u7279\u5F81\u56FE $\\widehat{G}$ \u7684\u7B2C m \u4E2A\u901A\u9053\u3002</p><p>Depthwise convolution\u5377\u79EF\u7684\u8BA1\u7B97\u590D\u6742\u5EA6\u4E3A:</p><p>$$D_{K} \\cdot D_{K} \\cdot M \\cdot D_{F} \\cdot D_{F}$$</p><p>\u76F8\u5BF9\u4E8E\u6807\u51C6\u5377\u79EF\uFF0Cfepthwise convolution\u5377\u79EF\u975E\u5E38\u6709\u6548\u3002 \u7136\u800C\uFF0C\u5B83\u53EA\u8FC7\u6EE4\u8F93\u5165\u901A\u9053(input depth)\uFF0C\u5E76\u6CA1\u6709\u7EC4\u5408\u5B83\u4EEC\u6765\u521B\u5EFA\u65B0\u529F\u80FD\u3002\u56E0\u6B64\uFF0C\u9700\u8981\u4E00\u4E2A\u989D\u5916\u7684\u5C42\uFF0C\u901A\u8FC7 pointwise convolution($1 \\times 1$ \u5377\u79EF\u6838)\u8BA1\u7B97depthwise convolution\u5377\u79EF\u8F93\u51FA\u7684\u7EBF\u6027\u53E0\u52A0\uFF0C\u7528\u6765\u751F\u6210\u65B0\u7279\u5F81\u3002</p><p>Depthwise Separable Convolution\u662F\u540C\u65F6\u7ED3\u5408 Depthwise convolution \u548C Pointwise convolution</p><p>Depthwise Separable Convolution\u7684\u8BA1\u7B97\u590D\u6742\u5EA6\u4E3A:</p><p>$$D_{K} \\cdot D_{K} \\cdot M \\cdot D_{F} \\cdot D_{F} + M \\cdot N \\cdot D_{F} \\cdot D_{F}$$</p><p>\u8FD9\u662Fdepthwise\u548C1\xD71 pointwise convolution\u7684\u603B\u548C\u3002</p><p>\u901A\u8FC7\u5C06\u5377\u79EF\u8868\u793A\u8FC7\u6EE4\u548Ccombine\u4E24\u4E2A\u8FC7\u7A0B\u5904\u7406\uFF0C\u53EF\u4EE5\u51CF\u5C11\u8BA1\u7B97\u91CF:</p><p>$$ \\dfrac{D_{K} \\cdot D_{K} \\cdot M \\cdot D_{F} \\cdot D_{F} + M \\cdot N \\cdot D_{F} \\cdot D_{F}}{D_{K} \\cdot D_{K} \\cdot M \\cdot N \\cdot D_{F} \\cdot D_{F}} = \\dfrac{1}{N} + \\dfrac{1}{D_{K}^{2}}$$</p><p>MobileNet \u53EF\u4EE5\u4F7F\u7528 $3\\times3$ Depthwise Separable Convolution\uFF0C\u5B83\u7684\u8BA1\u7B97\u590D\u6742\u5EA6\u6BD4\u6807\u51C6\u5377\u79EF\u4F4E8-9\u500D\uFF0C\u800C\u4E14\u51C6\u786E\u7387\u4EC5\u4F1A\u6709\u7565\u5FAE\u4E0B\u964D\u3002</p><h1 id="width-multiplier-thinner-models" tabindex="-1"><a class="header-anchor" href="#width-multiplier-thinner-models" aria-hidden="true">#</a> Width Multiplier: Thinner Models</h1><p>\u867D\u7136\u57FA\u672C\u7684 MobileNet \u67B6\u6784\u5DF2\u7ECF\u5F88\u5C0F\u4E14\u5EF6\u8FDF\u4F4E\uFF0C\u4F46\u5F88\u591A\u7279\u6B8A\u7528\u4F8B\u6216\u5E94\u7528\u4ECD\u9700\u8981\u6B64\u6A21\u578B\u66F4\u5C0F\u66F4\u5FEB\u3002\u4E3A\u4E86\u8FDB\u4E00\u6B65\u6784\u5EFA\u66F4\u5C0F\u548C\u66F4\u4F4E\u7684\u8BA1\u7B97\u5F00\u9500\u7684\u6A21\u578B\uFF0C\u6211\u4EEC\u5F15\u5165\u4E00\u4E2A\u975E\u5E38\u7B80\u5355\u7684\u53C2\u6570$\\alpha$\uFF0C\u79F0\u4E3Awidth multiplier\u3002\u5B83\u7684\u4F5C\u7528\u662F\u5728\u6BCF\u4E00\u5C42\u5747\u5300\u5730\u7ED9\u7F51\u7EDC\u7626\u8EAB\u3002\u5BF9\u4E8E\u4E00\u4E2A\u7ED9\u5B9A\u7684\u5C42\u548C$\\alpha$\uFF0C\u8F93\u5165\u901A\u9053\u6570$M$\u53D8\u6210$\\alpha M$\uFF0C\u8F93\u51FA\u901A\u9053\u6570$N$\u53D8\u6210$\\alpha N$\u3002</p><p>width multiplier\u7684Depthwise Separable Convolution\u7684\u8BA1\u7B97\u590D\u6742\u5EA6\u4E3A</p><p>$$D_{K} \\cdot D_{K} \\cdot \\alpha M \\cdot D_{F} \\cdot D_{F} + \\alpha M \\cdot \\alpha N \\cdot D_{F} \\cdot D_{F}$$</p><p>\u5176\u4E2D $\\alpha \\in (1,]$\uFF0C$\\alpha=1$ \u662F\u57FA\u7840MobileNet\uFF0C$\\alpha&lt;1$ \u662F\u7B80\u5316\u7684MobileNet\u3002</p><h1 id="\u4F18\u5316\u795E\u7ECF\u7F51\u7EDC" tabindex="-1"><a class="header-anchor" href="#\u4F18\u5316\u795E\u7ECF\u7F51\u7EDC" aria-hidden="true">#</a> \u4F18\u5316\u795E\u7ECF\u7F51\u7EDC</h1><p>3\uFF09\u9632\u6B62\u6A21\u578B\u8FC7\u62DF\u5408:</p><p>a. \u6DFB\u52A0Batch normalization\u5C42\uFF1A\u7528\u4EE5\u89C4\u8303\u7279\u5F81\u7684\u5206\u5E03\uFF0C\u4F7F\u8F93\u51FA\u7684\u7279\u5F81\u56FE\u5206\u5E03\u66F4\u52A0\u5747\u5300\u3002\u8FD9\u4E2A\u6548\u679C\u4E00\u822C\u4E0D\u9519\u3002</p><p>b. \u5BF9\u53C2\u6570\u505A\u6B63\u5219\u5316\uFF1A\u5176\u4ED6\u9632\u6B62\u6A21\u578B\u8FC7\u62DF\u5408\u7684\u7B56\u7565\u6BD4\u5982\u5BF9\u53C2\u6570\u505A\u6B63\u5219\u5316\uFF0C\u5305\u62ECweight, bias, BN beta\u548Cgamma\uFF0C\u8FD9\u4E9B\u53C2\u6570\u5360\u6A21\u578B\u6240\u6709\u53C2\u6570\u7684\u6BD4\u4F8B\u53EF\u80FD\u5F88\u5C0F\uFF0C\u6BD4\u5982\u5728AlexNet\u6A21\u578B\uFF0C\u5B83\u4EEC\u4EC5\u5360\u603B\u53C2\u6570\u91CF\u76840.02%\uFF0C\u5BF9\u8FD9\u4E9B\u53C2\u6570\u8FDB\u884C\u6B63\u5219\u5316\u4F1A\u589E\u52A0\u8BA1\u7B97\u91CF\uFF0C\u8FD8\u4F1A\u8BA9\u6A21\u578B\u635F\u5931\u4E00\u4E9B\u7075\u6D3B\u6027\u3002</p><p>c. dropout\uFF1Adropout\u53EF\u963B\u788D\u7F51\u7EDC\u5B66\u4E60\u4EC5\u5B58\u5728\u4E8E\u8BAD\u7EC3\u96C6\u4E2D\u5C40\u90E8\u7684\u201C\u989D\u5916\u89C4\u5F8B\u201D\uFF0C\u8FD9\u4E2A\u6548\u679C\u6BD4\u8F83\u5F3A\uFF0C\u4F46\u5982\u679C\u52A0\u7684dropout\u5C42\u8FC7\u591A\uFF0C\u6BD4\u5982\u6BCF\u5C42\u540E\u9762\u90FD\u52A0\u4E00\u4E2Adropout\uFF0C\u963B\u788D\u7F51\u7EDC\u5B66\u4E60\u89C4\u5F8B\u7684\u5F3A\u5EA6\u4E5F\u4F1A\u589E\u52A0\u3002</p><p>5\uFF09\u6743\u91CD\u521D\u59CB\u5316</p><p>\u6743\u91CD\u521D\u59CB\u5316\u51B3\u5B9A\u4E86\u7F51\u7EDC\u4ECE\u4EC0\u4E48\u4F4D\u7F6E\u5F00\u59CB\u8BAD\u7EC3\uFF0C\u826F\u597D\u7684\u8D77\u59CB\u4F4D\u7F6E\u4E0D\u4EC5\u53EF\u4EE5\u51CF\u5C11\u8BAD\u7EC3\u8017\u65F6\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u6A21\u578B\u7684\u8BAD\u7EC3\u66F4\u52A0\u7A33\u5B9A\uFF0C\u5E76\u4E14\u53EF\u4EE5\u907F\u5F00\u5F88\u591A\u8BAD\u7EC3\u4E0A\u7684\u95EE\u9898\u3002\u6BD4\u5982\u201Cdying ReLU\u201D\u95EE\u9898\uFF0CRelu\u53EF\u80FD\u4F1A\u4F7F\u8282\u70B9\u65E0\u6CD5\u88AB\u6FC0\u6D3B\uFF0C\u90A3\u4E48\u5C31\u9700\u8981\u5BF9w\u6743\u91CD\u8FDB\u884C\u5408\u7406\u7684\u521D\u59CB\u5316\uFF1B\u8FD8\u53EF\u4EE5\u4F7F\u7528leaky relu\u51FD\u6570\u89E3\u51B3\u3002</p><p>https://blog.csdn.net/qq_39573785/article/details/103845190</p>',42);function p(a,n){return i}var c=e(o,[["render",p],["__file","MobileNets-SSD.html.vue"]]);export{c as default};
